  @Test
  fun testTypeSolve() {
    val classUnderTest = PythonParser()
    val file = File(
        javaClass
        .classLoader
        .getResource("solve.py")!!
        .file
    )

    val input = classUnderTest.parse(file.path).root

    // print(input)

    val func = input.statements[0]
    assertTrue(func is FunctionDefinition)
    assertEquals(func.funcName.name, "func")
    val param = func.parameterList as Identifier
    assertEquals(param.name, "i")
    // get the first statement of the suite
    val call = func.suite
    assertTrue(call is Call)
    val callName = call.primary as Identifier
    assertEquals(callName.name, "print")

    val name = call.callTrailer
    assertTrue(name is Identifier)
    assertEquals("i", name.name)
  }

  @Test fun mainToStringTest () {
    val classUnderTest = PythonParser()
    val file = File(
        javaClass
            .classLoader
            .getResource("main.py")!!
            .file
    )

    val input = classUnderTest.parse(file.path).root
    assertNotNull(input)
    assertEquals(input.toString(), """FileInput(statements=[FunctionDefinition(
	"def" funcname=Identifier(
	name='func_no_arguments'
) "(" parameters=ParameterList(parameters=[]) ")" ":" suite =Call(
	primary=Identifier(
	name='print'
) callTrailer=StringLiteral(
	value=test
)
)
), FunctionDefinition(
	"def" funcname=Identifier(
	name='func_one_argument'
) "(" parameters=Identifier(
	name='i'
) ")" ":" suite =Call(
	primary=Identifier(
	name='print'
) callTrailer=Identifier(
	name='i'
)
)
), FunctionDefinition(
	"def" funcname=Identifier(
	name='func_two_arguments'
) "(" parameters=ParameterList(parameters=[Identifier(
	name='i'
), Identifier(
	name='j'
)]) ")" ":" suite =Suite(
	statements=[Call(
	primary=Identifier(
	name='print'
) callTrailer=Identifier(
	name='i'
)
), Call(
	primary=Identifier(
	name='print'
) callTrailer=Identifier(
	name='j'
)
)]
)
)])""".replace("\n", System.lineSeparator()))
  }

